const Todo = ({todo, index, completeTask, removeTask}) => {

  return (
      <div id={todo.id} key={todo.key} className={todo.complete ? "todo strike" : "todo "}>
        <p>
          {todo.key}
        </p>
        <div className={'btnWrap'}>
          <button className={'btn'} onClick={() => completeTask(index)}>
            Complete
          </button>
          <button className={'btn'} onClick={() => removeTask(index)}>
            X
          </button>
        </div>
      </div>
  );
};

export default Todo;
