import Todo from "./Todo/Todo";
import {createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";

const TodoList = ({toDoList, handleFilter, completeTask, removeTask}) => {

      let theme = createTheme({
        typography: {
          body1: {
            width: '70%',
            fontWeight: 500,
            fontSize: '1.1em',
          },
        }
      })
      theme = responsiveFontSizes(theme);

      let toDo = toDoList.map((todo, index) => {
        return (
            <Todo todo={todo} index={index} completeTask={completeTask} removeTask={removeTask}/>
        );
      })

      return (
          <div className='todoWrap'>
            <ThemeProvider theme={theme}>
              <Typography variant="h6" component="h6" gutterBottom>
                {toDo}
              </Typography>
              <button className={'btnClear'} onClick={handleFilter}>Clear completed</button>
            </ThemeProvider>
          </div>
      );
    }
;

export default TodoList;
