import Checkbox from '@mui/material/Checkbox';
import Stack from '@mui/material/Stack';
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import DoneIcon from '@mui/icons-material/Done';
import {createTheme, Input, ThemeProvider} from '@mui/material';
import {useState} from "react";

const TodoItem = ({todo, completeState, toggleEditState, completeTask, removeTask, saveEditTask}) => {

  const [userEdit, setUserEdit] = useState(todo.task);

  const toggleEditTodo = (id) => {
    toggleEditState(id)
  }

  const saveEditTodo = (id) => {
    saveEditTask(id, userEdit)
  }


  const handleChange = e => {
    setUserEdit(e.currentTarget.value)
  }


  const strikeCompleted = completeState ? "todo strikeTask" : "todo";
  const deleteCompleted = completeState ? "primary" : "default";
  const toggleShowInput = todo.edit ? "showInput" : "hideInput";
  const toggleHideText = todo.edit ? "hideText" : "showText";
  const hideEditButton = todo.edit ? "hideEditButton" : "showEditButton";
  const hideSaveButton = todo.edit ? "showSaveButton" : "hideSaveButton";
  const disableCheckbox = !!todo.edit

  const theme = createTheme({
    components: {
      MuiInput: {
        styleOverrides: {
          root: {
            width: '150%',
          },
        },
      },
    },
  });

  return (
      <div id={todo.id} className={strikeCompleted}>
        <Checkbox checked={completeState}
                  disabled={disableCheckbox}
                  color="success"
                  onClick={() => completeTask(todo.id)
                  }/>
        <div className={hideEditButton}>
          <IconButton aria-label="delete"
                      onClick={() => toggleEditTodo(todo.id)
                      }>
            <EditIcon/>
          </IconButton>
        </div>
        <div className={hideSaveButton}>
          <IconButton aria-label="save"
                      onClick={() => saveEditTodo(todo.id, todo.task)}
                      >
            <DoneIcon/>
          </IconButton>
        </div>
        <p className={toggleHideText}>
          {todo.task}
        </p>
        <div className={toggleShowInput}>
          <ThemeProvider theme={theme}>
            <Input value={userEdit}
                   type="text"
                   size='small'
                   onChange={handleChange}
            />
          </ThemeProvider>
        </div>
        <div className={'btnWrap'}>
          <Stack direction="row"
                 spacing={1}>
            <IconButton onClick={() => removeTask(todo.id)}
                        aria-label="delete"
                        color={deleteCompleted}
                        disabled={disableCheckbox}
            >
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default TodoItem;
