import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import DeleteIcon from '@mui/icons-material/Delete';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';

const Todo = ({todo, index, completeTask, removeTask, handleEditing}) => {

  const isComplete = todo.complete ? "todo strike" : "todo";
  const isDelete = todo.complete ? "primary" : "default";

  return (
      <div className={isComplete}>
        <FormGroup>
          <FormControlLabel
              control={<Checkbox checked={todo.complete}
                                 color="success"
              />}
              onChange={() => completeTask(index)} label={''}
          />
        </FormGroup>
        <p className={'todosText'} onDoubleClick={handleEditing}>
          {todo.task}
        </p>
        <div className={'btnWrap'}>
          <Stack direction="row"
                 spacing={1}>
            <IconButton onClick={() => removeTask(index)}
                        aria-label="delete"
                        color={isDelete}>
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default Todo;
