import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Stack from '@mui/material/Stack';
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import {createTheme, Input, ThemeProvider} from '@mui/material';
import {useState} from "react";

const TodoItem = ({todo, task, completeState, handleEditState, completeTask, removeTask}) => {

  const [userInput, setUserInput] = useState('');

  const handleEditChange = e => {
    setUserInput(e.target.value);
  }

  const isComplete = completeState ? "todo strike" : "todo";
  const isDelete = completeState ? "primary" : "default";
  const showInput = todo.edit ? "showInput" : "hideInput";
  const hideText = todo.edit ? "hideText" : "showText";
  const disableInput = !completeState;
  const disableState = !todo.edit

  const theme = createTheme({
    components: {
      MuiInput: {
        styleOverrides: {
          root: {
            width: '150%',
          },
        },
      },
    },
  });

  return (
      <div id={todo.id} className={isComplete}>
        <Checkbox checked={completeState}
                  disabled={disableState}
                  color="success"
                  onClick={() => completeTask(todo.id)
        }/>
        <div>
          <IconButton disabled={disableInput}
                      aria-label="delete">
            <EditIcon/>
          </IconButton>
        </div>
        <p className={hideText}>
          {userInput}
        </p>
        <div className={showInput}>
          <ThemeProvider theme={theme}>
            <Input 
                   value={userInput}
                   type="text"
                   size='small'/>
          </ThemeProvider>
        </div>
        <div className={'btnWrap'}>
          <Stack direction="row"
                 spacing={1}>
            <IconButton onClick={() => removeTask(todo.id)}
                        aria-label="delete"
                        color={isDelete}
                        disabled={disableState}
            >
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default TodoItem;
