import {useState} from 'react';
import data from "./data.json";
import './App.css';
import Header from "./components/Header/Header";
import TodoForm from "./components/TodosForm/TodoForm";
import TodosList from "./components/TodosList/TodosList";

const App = () => {
  const [toDoList, setToDoList] = useState(data);


  const handleEditState = index => {
    const newTodos = [...toDoList];
    newTodos[index].edit = !newTodos[index].edit;
    setToDoList(newTodos);
  }

  const clearCompleted = () => {
    let filtered = toDoList.filter(task => {
      return !task.complete;
    });
    setToDoList(filtered);
  };

  function completeTask(id) {
    const newTodo = [...toDoList].filter(task => {
      if (task.id === id) {
        task.complete = !task.complete;
      }
    })
    setToDoList(newTodo);
  }

  const addTask = userInput => {
    let newTodo = [...toDoList, {
      id: toDoList.length + 1,
      task: userInput,
      complete: false,
      checked: false,
      edit: false
    }];
    setToDoList(newTodo);
  }

  const removeTask = id => {
    const newTodo = [...toDoList].filter(task => task.id !== id);
    setToDoList(newTodo);
  }

  return (
      <div className="App">
        <Header/>
        <TodoForm addTask={addTask}/>
        <TodosList toDoList={toDoList}
                   handleEditState={handleEditState}
                   completeTask={completeTask}
                   removeTask={removeTask}
                   clearCompleted={clearCompleted}
        />
      </div>
  );
};

export default App;
