import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Stack from '@mui/material/Stack';
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import {createTheme, Input, ThemeProvider} from '@mui/material';
import {useState} from 'react';

const TodoItem = ({todo, index, handleEdit, completeTask, removeTask, editTask}) => {

  const [editInput, setEditInput] = useState('');

  const handleSubmit = e => {
    e.preventDefault()
    if (!editInput.trim()) return;
    editTask(editInput);
    setEditInput('');
  };

  const handleChange = e => {
    if (e.key === 'enter') setEditInput(e.target.value)
  }

  const isComplete = todo.complete ? "todo strike" : "todo";
  const isDelete = todo.complete ? "primary" : "default";
  const showInput = todo.edit ? "showInput" : "hideInput";
  const hideText = todo.edit ? "hideText" : "showText";
  const disableInput = !!todo.complete;
  const disableState = !!todo.edit;


  const theme = createTheme({
    components: {
      MuiInput: {
        styleOverrides: {
          root: {
            width: '150%',
          },
        },
      },
    },
  });

  return (
      <div className={isComplete}>
        <FormGroup>
          <FormControlLabel control={<Checkbox checked={todo.complete} color="success"/>}
                            disabled={disableState}
                            onChange={() => completeTask(index)}
                            label={''}
                            className={"checkBox"}
                            sx={{marginRight: 0}
                            }/>
        </FormGroup>
        <div>
          <IconButton disabled={disableInput} onClick={() => handleEdit(index)} aria-label="delete">
            <EditIcon/>
          </IconButton>
        </div>
        <p className={hideText}>
          {todo.task}
        </p>
        <div className={showInput}>
          <ThemeProvider theme={theme}>
            <Input onKeyDown={handleChange} value={editInput} type="text" size='small'/>
          </ThemeProvider>
        </div>
        <div className={'btnWrap'}>
          <Stack direction="row" spacing={1}>
            <IconButton onClick={() => removeTask(index)}
                        aria-label="delete"
                        color={isDelete}
                        disabled={disableState}
            >
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default TodoItem;
