import TodoItem from "./TodoItem/TodoItem";
import {Button, createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";
import Stack from "@mui/material/Stack";
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from "@mui/material/IconButton";
import {useState} from "react";


const TodosList = ({todoList, clearCompleted, toggleEditState, completeTask, removeTask, saveEditTask}) => {

  const [filtered, setFiltered] = useState(todoList)

  const todoFilter = status => {
    if (status === 'all') {
      setFiltered(todoList)
    } else {
      let newTodo = [...todoList].filter(task => task.complete === status)
      setFiltered(newTodo)
    }
  }

  const todoItem = filtered.map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })
  const completedItems = todoList.filter(task => task.complete === true).map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })
  const activeItems = todoList.filter(task => task.complete === false).map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })

  let theme = createTheme({
    typography: {
      body1: {
        width: '70%',
        fontWeight: 500,
        fontSize: '1.1em',
      },
    }
  })
  theme = responsiveFontSizes(theme);

  return (
      <div>
        <ThemeProvider theme={theme}>
          <Typography variant="h6" component="h6" gutterBottom>
            {todoItem}
          </Typography>
          <Stack direction={"row"}
                 justifyContent={"center"}
                 alignItems={'flex-start'}
                 spacing={3}
          >
            <Button variant="text"
                    size={'large'}
                    onClick={() => todoFilter('all')}>
              All {filtered.length}
            </Button>
            <Button variant="text"
                    size={'large'}
                    onClick={() => todoFilter(false)}>
              Active {activeItems.length}
            </Button>
            <Button variant="text"
                    color="success"
                    size={'large'}
                    onClick={() => todoFilter(true)}>
              Completed {completedItems.length}
            </Button>
            <IconButton onClick={clearCompleted}
                        aria-label="delete"
                        color={'primary'}
                        size={'medium'}>
              <DeleteIcon fontSize="inherit"/>
            </IconButton>
          </Stack>
        </ThemeProvider>
      </div>
  );
};

export default TodosList;
