import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import DeleteIcon from '@mui/icons-material/Delete';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';

const Todo = ({todo, index, completeTask, removeTask, handleToggle}) => {

  const isComplete = todo.checked ? "todo strike" : "todo";

  const handleClick = (e) => {
    e.preventDefault()
    handleToggle(e.currentTarget.key)
  }

  return (
      <div className={isComplete}>
        <FormGroup>
          <FormControlLabel control={<Checkbox checked={todo.checked} color="success"/>} onChange={() => completeTask(index)}/>
        </FormGroup>
        <p className={'todosText'}>
          {todo.task}
        </p>
        <div className={'btnWrap'}>
          <Stack direction="row" spacing={1}>
            <IconButton onClick={() => removeTask(index)} aria-label="delete">
              <DeleteIcon />
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default Todo;
