import TodoItem from "./TodoItem/TodoItem";
import {createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";
import {useState} from "react";
import ActiveItem from "./FilteredItem/ActiveItem";
import CompletedItem from "./FilteredItem/CompletedItem";

const TodosList = ({toDoList, clearCompleted, handleEditState, completeTask, removeTask}) => {

  const [active, setActive] = useState('none');
  const [completed, setCompleted] = useState('none');
  const [all, setAll] = useState('block');

  let toDoItems = toDoList.map((todo, index) => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  index={index}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  handleEditState={handleEditState}
        />
    );
  })

  let activeList = toDoList.filter(task => task.complete === false).map((active, index) => {
    return (
        <ActiveItem key={active.id}
                    active={active}
                    index={index}
                    completeTask={completeTask}
                    removeTask={removeTask}
                    handleEditState={handleEditState}
        />
    );
  })

  let completedList = toDoList.filter(task => task.complete === true).map((completed, index) => {
    return (
        <CompletedItem key={completed.id}
                       completed={completed}
                       index={index}
                       completeTask={completeTask}
                       removeTask={removeTask}
                       handleEditState={handleEditState}
        />
    );
  })

  const activeState = () => {
    setActive('block');
    setCompleted('none')
    setAll('none')
  }

  const completedState = () => {
    setActive('none');
    setCompleted('block')
    setAll('none')
  }

  const allState = () => {
    setActive('none');
    setCompleted('none')
    setAll('block')
  }


  let theme = createTheme({
    typography: {
      body1: {
        width: '70%',
        fontWeight: 500,
        fontSize: '1.1em',
      },
    }
  })
  theme = responsiveFontSizes(theme);

  return (
      <div>
        <ThemeProvider theme={theme}>
          <Typography display={all} className={'hideActive'} variant="h6" component="h6" gutterBottom>
            {toDoItems}
          </Typography>
          <Typography display={active} variant="h6" component="h6" gutterBottom>
            {activeList}
          </Typography>
          <Typography display={completed} variant="h6" component="h6" gutterBottom>
            {completedList}
          </Typography>
          <button className={'btnClear'} onClick={allState}>
            All {toDoItems.length}
          </button>
          <button className={'btnClear'} onClick={activeState}>
            Active {activeList.length}
          </button>
          <button className={'btnClear'} onClick={completedState}>
            Completed {completedList.length}
          </button>
          <button className={'btnClear'} onClick={clearCompleted}>
            Clear Completed
          </button>
        </ThemeProvider>
      </div>
  );
};

export default TodosList;
