import {useState} from 'react';
import data from "./data.json";
import './App.css';
import Header from "./components/Header/Header";
import TodoForm from "./components/TodosForm/TodoForm";
import TodosList from "./components/TodosList/TodosList";

const App = () => {
  const [toDoList, setToDoList] = useState(data);
  const [isEditing, setIsEditing] = useState(false);
  const [currentTodo, setCurrentTodo] = useState({});

  function handleEditInputChange(e) {
    // set the new state value to what's currently in the edit input box
    setCurrentTodo({ ...currentTodo, task: e.target.value });
    console.log(currentTodo);
  }

  const handleEditState = index => {
    const newTodos = [...toDoList];
    newTodos[index].edit = !newTodos[index].edit;
    setToDoList(newTodos);
  }

  const handleFilter = () => {
    let filtered = toDoList.filter(task => {
      return !task.complete;
    });
    setToDoList(filtered);
  };

  const completeTask = index => {
    const newTodos = [...toDoList];
    newTodos[index].complete = !newTodos[index].complete;
    setToDoList(newTodos);
  }

  const addTask = userInput => {
    let copy = [...toDoList];
    copy = [...copy, {id: toDoList.length + 1, task: userInput, complete: false, checked: false, edit: false}];
    setToDoList(copy);
  }

  const removeTask = index => {
    const newTodos = [...toDoList];
    newTodos.splice(index, 1);
    setToDoList(newTodos);
  }

  return (
      <div className="App">
        <Header/>
        <TodoForm addTask={addTask}/>
        <TodosList toDoList={toDoList}
                   handleFilter={handleFilter}
                   handleEditState={handleEditState}
                   completeTask={completeTask}
                   removeTask={removeTask}
        />
      </div>
  );
};

export default App;
