import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import DeleteIcon from '@mui/icons-material/Delete';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';

const TodoItem = ({todo, index, completeTask, removeTask, handleEdit}) => {

  const isComplete = todo.complete ? "todo strike" : "todo";
  const isDelete = todo.complete ? "primary" : "default";
  const isEdit = todo.complete ? "show-input" : "hide-input"

  return (
      <div className={isComplete}>
        <FormGroup>
          <FormControlLabel
              control={<Checkbox checked={todo.complete} color="success"/>}
              onChange={() => completeTask(index)} label={''}/>
        </FormGroup>
        <button onClick={() => handleEdit(index)}>Edit</button>
        <p className={'todosText'}>
          {todo.task}
        </p>
        <input type="text" className={'hide-input'}/>
        <div className={'btnWrap'}>
          <Stack direction="row" spacing={1}>
            <IconButton onClick={() => removeTask(index)}
                        aria-label="delete"
                        color={isDelete}>
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default TodoItem;
