import {createTheme, Input, ThemeProvider} from '@mui/material';


const Form = (props) => {

  const theme = createTheme({
    components: {
      // Name of the component
      MuiInput: {
        styleOverrides: {
          // Name of the slot
          root: {
            // Some CSS
            fontSize: '1rem',
          },
        },
      },
    },
  });

  return (
      <form>
        <ThemeProvider theme={theme}>
        <Input size='medium' placeholder='Add a task'/>
        </ThemeProvider>
      </form>
  );
}

export default Form;
