import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import DeleteIcon from '@mui/icons-material/Delete';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import {useState} from 'react';


const Todo = ({todo, index, completeTask, removeTask}) => {
  const [checked, setChecked] = useState(false);

  const handleChange = (event) => {
    if  (todo.complete)
    setChecked(event.target.checked);
  };

  return (
      <div id={todo.id} key={todo.key} className={todo.complete ? "todo strike" : "todo "}>
        <FormGroup>
          <FormControlLabel control={<Checkbox checked={checked} color="success"/>} onChange={handleChange} label={''}/>
        </FormGroup>
        <p className={'todosText'}>
          {todo.key}
        </p>
        <div className={'btnWrap'}>
          <Stack direction="row" spacing={1}>
            <IconButton onClick={() => removeTask(index)} aria-label="delete">
              <DeleteIcon />
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default Todo;
