import TodoItem from "./TodoItem/TodoItem";
import {createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";
import {useState} from "react";

const TodosList = ({toDoList, clearCompleted, handleEditState, completeTask, removeTask}) => {

      const [hide, setHide] = useState('none')
      const [show, setShow] = useState('block')

      let toDoItem = toDoList.map((todo, index) => {
        return (
            <TodoItem key={todo.id}
                      todo={todo}
                      index={index}
                      completeTask={completeTask}
                      removeTask={removeTask}
                      handleEditState={handleEditState}
            />
        );
      })

      let filterComplete = toDoList.filter(task => task.complete === true).map((todo, index) => {
        return (
            <TodoItem key={todo.id}
                      todo={todo}
                      index={index}
                      completeTask={completeTask}
                      removeTask={removeTask}
                      handleEditState={handleEditState}
            />
        );
      })

      const classToggle = () => {
          if (hide === 'none') {
            setHide('block')
          }
          if (show === 'block') {
            setShow('none')
          }
      }


      let theme = createTheme({
        typography: {
          body1: {
            width: '70%',
            fontWeight: 500,
            fontSize: '1.1em',
          },
        }
      })
      theme = responsiveFontSizes(theme);

      return (
          <div>
            <ThemeProvider theme={theme}>
              <Typography display={show} className={'hideActive'} variant="h6" component="h6" gutterBottom>
                {toDoItem}
              </Typography>
              <Typography display={hide} variant="h6" component="h6" gutterBottom>
                {filterComplete}
              </Typography>
              <button className={'btnClear'} onClick={clearCompleted}>
                Clear Active
              </button>
              <button className={'btnClear'} onClick={classToggle}>
                Active
              </button>
            </ThemeProvider>
          </div>
      );
    }
;

export default TodosList;
