import TodoItem from "./TodoItem/TodoItem";
import {createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";
import {useState} from "react";

const TodosList = ({toDoList, clearCompleted, handleEditState, completeTask, removeTask}) => {

      const [active, setactive] = useState('none');
      const [completed, setCompleted] = useState('block');

      let toDoItem = toDoList.map((todo, index) => {
        return (
            <TodoItem key={todo.id}
                      todo={todo}
                      index={index}
                      completeTask={completeTask}
                      removeTask={removeTask}
                      handleEditState={handleEditState}
            />
        );
      })

      let completedList = toDoList.filter(task => task.complete === false).map((todo, index) => {
        return (
            <TodoItem key={todo.id}
                      todo={todo}
                      index={index}
                      completeTask={completeTask}
                      removeTask={removeTask}
                      handleEditState={handleEditState}
            />
        );
      })




      let theme = createTheme({
        typography: {
          body1: {
            width: '70%',
            fontWeight: 500,
            fontSize: '1.1em',
          },
        }
      })
      theme = responsiveFontSizes(theme);

      return (
          <div>
            <ThemeProvider theme={theme}>
              <Typography display={completed} className={'hideActive'} variant="h6" component="h6" gutterBottom>
                {toDoItem}
              </Typography>
              <Typography display={active} variant="h6" component="h6" gutterBottom>
                {completedList}
              </Typography>
              <button className={'btnClear'}>
                All
              </button>
              <button className={'btnClear'} onClick={classToggle}>
                Active
              </button>
              <button className={'btnClear'}>
                Completed
              </button>
              <button className={'btnClear'} onClick={clearCompleted}>
                Clear Completed
              </button>
            </ThemeProvider>
          </div>
      );
    }
;

export default TodosList;
