import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import DeleteIcon from '@mui/icons-material/Delete';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import EditIcon from '@mui/icons-material/Edit';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';

const TodoItem = ({todo, index, completeTask, removeTask, handleEdit}) => {

  const isComplete = todo.complete ? "todo strike" : "todo";
  const isDelete = todo.complete ? "primary" : "default";
  const showInput = todo.edit ? "showInput" : "hideInput";
  const hideText = todo.edit ? "hideText" : "showText";
  const hideMaterial = todo.edit ? {display: 'none'} : {display: "block"}

  return (
      <div className={isComplete}>
        <FormGroup>
          <FormControlLabel control={<Checkbox checked={todo.complete} color="success"/>}
                            onChange={() => completeTask(index)}
                            label={''}
                            className={"checkBox"}
                            sx={{marginRight: 0}}
          />
        </FormGroup>
        <div>
          <IconButton onClick={() => handleEdit(index)}
                      aria-label="delete">
            <EditIcon/>
          </IconButton>
        </div>
        <p className={hideText}>
          {todo.task}
        </p>
        <input type="text" className={showInput}/>
        <Box
            component="form"
            sx={{hideMaterial}}
            noValidate
            autoComplete="off"
        >
          <TextField id="standard-basic" label="Standard" variant="standard"/>
        </Box>
        <div className={'btnWrap'}>
          <Stack direction="row" spacing={1}>
            <IconButton onClick={() => removeTask(index)}
                        aria-label="delete"
                        color={isDelete}>
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default TodoItem;
