import {useState} from 'react';
//mock data
import data from "./data.json";
//style
import './App.css';
//components
import Header from "./components/Header/Header";
import TodoForm from "./components/Form/TodoForm";
import TodoList from "./components/TodoList/TodoList";

const App = () => {
  const [toDoList, setToDoList] = useState(data);

  const handleFilter = () => {
    let filtered = toDoList.filter(task => {
      return !task.complete;
    });
    setToDoList(filtered);
  };

  const completeTask = index => {
    const newTodos = [...toDoList];
    newTodos[index].complete = true;
    setToDoList(newTodos);
  }

  const handleToggle = (id) => {
    let mapped = toDoList.map(task => {
      return task.id === id ? { ...task, complete: !task.complete } : { ...task};
    });
    setToDoList(mapped);
  }

  const addTask = userInput => {
    let copy = [...toDoList];
    copy = [...copy, {id: toDoList.length + 1, key: userInput, complete: false}];
    setToDoList(copy);
  }

  const removeTask = index => {
    const newTodos = [...toDoList];
    newTodos.splice(index, 1);
    setToDoList(newTodos);
  }
  return (
      <div className="App">
        <Header />
        <TodoForm addTask={addTask}/>
        <TodoList toDoList={toDoList} handleFilter={handleFilter} completeTask={completeTask} handleToggle={handleToggle} removeTask={removeTask}/>
      </div>
  );
};

export default App;
