import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import DeleteIcon from '@mui/icons-material/Delete';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Button from '@mui/material/Button';

const TodoItem = ({todo, index, completeTask, removeTask, handleEdit}) => {

  const isComplete = todo.complete ? "todo strike" : "todo";
  const isDelete = todo.complete ? "primary" : "default";
  const showInput = todo.edit ? "showInput" : "hideInput";
  const hideText = todo.edit ? "hideText" : "showText";

  return (
      <div className={isComplete}>
        <FormGroup>
          <FormControlLabel
              control={<Checkbox checked={todo.complete} color="success"/>}
              onChange={() => completeTask(index)} label={''}/>
        </FormGroup>
        <div>
          <Button variant="outlined" startIcon={<DeleteIcon />}>
            Delete
          </Button>
          <button onClick={() => handleEdit(index)}>
            Edit
          </button>
        </div>
        <p className={hideText}>
          {todo.task}
        </p>
        <input type="text" className={showInput}/>
        <div className={'btnWrap'}>
          <Stack direction="row" spacing={1}>
            <IconButton onClick={() => removeTask(index)}
                        aria-label="delete"
                        color={isDelete}>
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default TodoItem;
