import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Stack from '@mui/material/Stack';
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import {createTheme, Input, ThemeProvider} from '@mui/material';
import {useState} from "react";

const CompletedItem = ({completed, index, handleEditState, completeTask, removeTask}) => {

  const [userEdit, setUserEdit] = useState(completed.task);

  const handleEditChange = e => {
    setUserEdit(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!userEdit.trim()) return;
    completed.edit = false;
    setUserEdit(userEdit + ' ');
  }

  const isComplete = completed.complete ? "todo strike" : "todo";
  const isDelete = completed.complete ? "primary" : "default";
  const showInput = completed.edit ? "showInput" : "hideInput";
  const hideText = completed.edit ? "hideText" : "showText";
  const disableInput = !completed.complete;
  const disableState = !!completed.edit;

  const theme = createTheme({
    components: {
      MuiInput: {
        styleOverrides: {
          root: {
            width: '150%',
          },
        },
      },
    },
  });

  return (
      <div className={isComplete}>
        <FormGroup>
          <FormControlLabel control={<Checkbox checked={completed.complete} color="success"/>}
                            disabled={disableState}
                            onChange={() => completeTask(index)}
                            label={''}
                            className={"checkBox"}
                            sx={{marginRight: 0}
                            }/>
        </FormGroup>
        <div>
          <IconButton disabled={disableInput} onClick={() => handleEditState(index)} aria-label="delete">
            <EditIcon/>
          </IconButton>
        </div>
        <p className={hideText}>
          {userEdit}
        </p>
        <div className={showInput}>
          <form onSubmit={handleSubmit}>
            <ThemeProvider theme={theme}>
              <Input onChange={handleEditChange} value={userEdit} type="text" size='small'/>
            </ThemeProvider>
          </form>
        </div>
        <div className={'btnWrap'}>
          <Stack direction="row" spacing={1}>
            <IconButton onClick={() => removeTask(index)}
                        aria-label="delete"
                        color={isDelete}
                        disabled={disableState}
            >
              <DeleteIcon/>
            </IconButton>
          </Stack>
        </div>
      </div>
  );
};

export default CompletedItem;
