import TodoItem from "./TodoItem/TodoItem";
import {Button, createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";
import Stack from "@mui/material/Stack";

const TodosList = ({toDoList, clearCompleted, toggleEditState, completeTask, removeTask, saveEditTask}) => {

  let todoItems = toDoList.map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })
  let completedItems = toDoList.filter(task => task.complete === true).map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })
  let activeItems = toDoList.filter(task => task.complete === false).map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })


  let theme = createTheme({
    typography: {
      body1: {
        width: '70%',
        fontWeight: 500,
        fontSize: '1.1em',
      },
    }
  })
  theme = responsiveFontSizes(theme);

  return (
      <div>
        <ThemeProvider theme={theme}>
          <Typography variant="h6" component="h6" gutterBottom>
            {todoItems}
          </Typography>
          <Stack direction={"row"} justifyContent={"center"} spacing={2}>
            <Button className={'btn'} variant="text">
              All {todoItems.length}
            </Button>
            <Button className={'btn'} variant="text">
              Completed {completedItems.length}
            </Button>
            <Button className={'btn'} variant="text">
              Active {activeItems.length}
            </Button>
            <Button className={'btn'} variant="text" onClick={clearCompleted}>
              Clear Completed
            </Button>
          </Stack>
        </ThemeProvider>
      </div>
  );
};

export default TodosList;
