import TodoItem from "./TodoItem/TodoItem";
import {createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";

const TodosList = ({toDoList, clearCompleted, toggleEditState, completeTask, removeTask, saveEditTask}) => {

  let toDoItems = toDoList.map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })

  let theme = createTheme({
    typography: {
      body1: {
        width: '70%',
        fontWeight: 500,
        fontSize: '1.1em',
      },
    }
  })
  theme = responsiveFontSizes(theme);

  return (
      <div>
        <ThemeProvider theme={theme}>
          <Typography variant="h6" component="h6" gutterBottom>
            {toDoItems}
          </Typography>
          <button className={'btn'}>
            All {toDoItems.length}
          </button>
          <button className={'btn'}>
            Active {toDoItems.complete}
          </button>
          <button className={'btn'}>
            Completed
          </button>
          <button className={'btn'} onClick={clearCompleted}>
            Clear Completed
          </button>
        </ThemeProvider>
      </div>
  );
};

export default TodosList;
