import TodoItem from "./TodoItem/TodoItem";
import {Box, Button} from '@mui/material';
import Typography from "@mui/material/Typography";
import Stack from "@mui/material/Stack";
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from "@mui/material/IconButton";
import LibraryAddCheckIcon from '@mui/icons-material/LibraryAddCheck';
import {useState} from "react";


const TodosList = (props) => {

  const {
    todoList,
    clearCompleted,
    toggleEditState,
    completeTask,
    completeAllTasks,
    removeTask,
    saveEditTask
  } = props

  const [displayAllTodos, setDisplayAllTodos] = useState('block');
  const [focusAllButton, setFocusAllButton] = useState("outlined");
  const [displayCurrentTodos, setDisplayCurrentTodos] = useState('none');
  const [focusCurrentButton, setFocusCurrentButton] = useState(false);
  const [displayCompletedTodos, setDisplayCompletedTodos] = useState('none');
  const [focusCompletedButton, setFocusCompletedButton] = useState("outlined");

  const showAllTodos = () => {
    setDisplayAllTodos('block');
    setDisplayCompletedTodos('none');
    setDisplayCurrentTodos('none');

    setFocusAllButton('contained')
    setFocusCompletedButton('outlined')
    setFocusCurrentButton(true)
  }

  const showCurrentTodos = () => {
    setDisplayAllTodos('none');
    setDisplayCompletedTodos('none');
    setDisplayCurrentTodos('block');

    setFocusAllButton('outlined')
    setFocusCompletedButton('outlined')
    setFocusCurrentButton(false)
  }

  const showCompletedTodos = () => {
    setDisplayAllTodos('none');
    setDisplayCompletedTodos('block');
    setDisplayCurrentTodos('none');

    setFocusAllButton('outlined')
    setFocusCompletedButton('contained')
    setFocusCurrentButton(true)
  }

  const todoItem = todoList.map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })
  const completedItems = todoList.filter(task => task.complete === true).map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })
  const currentItems = todoList.filter(task => task.complete === false).map(todo => {
    return (
        <TodoItem key={todo.id}
                  todo={todo}
                  completeState={todo.complete}
                  completeTask={completeTask}
                  removeTask={removeTask}
                  saveEditTask={saveEditTask}
                  toggleEditState={toggleEditState}
        />
    );
  })

  const disableDeleteButton = !completedItems.length;
  const disableCompleteButton = !todoItem.length

  return (
      <div>
        <Box sx={{
          height: '70vh',
          overflowY: 'scroll'
        }}>
          {/*<Typography variant="h6"
                      component="h6"
                      gutterBottom
                      display={displayAllTodos}
          >
            {todoItem}
          </Typography>
          <Typography variant="h6"
                      component="h6"
                      gutterBottom
                      display={displayCompletedTodos}
          >
            {completedItems}
          </Typography>
          <Typography variant="h6"
                      component="h6"
                      gutterBottom
                      display={displayCurrentTodos}
          >
            {currentItems}
          </Typography>*/}
        </Box>
        <Stack direction={"row"}
               justifyContent={"center"}
               alignItems={'flex-start'}
               spacing={3}
        >
          <IconButton onClick={completeAllTasks}
                      aria-label="mark-all"
                      color={'success'}
                      size={'medium'}
                      disabled={disableCompleteButton}
          >
            <LibraryAddCheckIcon fontSize="inherit"/>
          </IconButton>
          <Button variant={focusAllButton}
                  size={'large'}
                  onClick={showAllTodos}
          >
            All {todoList.length}
          </Button>
          <Button variant={focusCurrentButton? 'outlined' : 'contained'}
                  color={'info'}
                  size={'large'}
                  onClick={showCurrentTodos}
          >
            Current {currentItems.length}
          </Button>
          <Button variant={focusCompletedButton}
                  color="success"
                  size={'large'}
                  onClick={showCompletedTodos}
          >
            Completed {completedItems.length}
          </Button>
          <IconButton onClick={clearCompleted}
                      aria-label="delete"
                      color={'error'}
                      size={'medium'}
                      disabled={disableDeleteButton}
          >
            <DeleteIcon fontSize="inherit"/>
          </IconButton>
        </Stack>
      </div>
  );
};

export default TodosList;
