import Todo from "./Todo/Todo";
import {createTheme, responsiveFontSizes, ThemeProvider} from '@mui/material';
import Typography from "@mui/material/Typography";

const TodoList = ({toDoList, handleFilter, completeTodo}) => {

  let theme = createTheme({
    typography: {
      body1: {
        fontWeight: 500,
      },
      button: {
        fontStyle: 'italic',
      }}})
  theme = responsiveFontSizes(theme);

  let todos = toDoList.map((todo, index) => {
    return (
        <Todo todo={todo} index={index} handleFilter={handleFilter} completeTodo={completeTodo}/>
    );
  })

  return (
      <ThemeProvider theme={theme}>
        <div className='todoWrap'>
          <Typography variant="body1" component="h1" gutterBottom>
            {todos}
          </Typography>
          <button className={'btn'} onClick={handleFilter}>Clear completed</button>
        </div>
      </ThemeProvider>
  );
}
  ;

  export default TodoList;
